import collections
class Solution:
    def maxAreaOfIsland(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        def connect(grid):
            index = 0
            seen = [[False for c in range(n)] for r in range(m)]

            for r in range(m):
                for c in range(n):
                    if not seen[r][c] and grid[r][c] != 0:
                        index += 1
                        bfs(grid, seen, index, [(r, c)])

        def bfs(grid, seen, index, lst):
            r, c = lst[0]
            grid[r][c], seen[r][c] = index, True
            for r, c in lst:
                for dr, dc in ((1, 0), (-1, 0), (0, -1), (0, 1)):
                    if 0 <= r+dr < m and 0 <= c+dc < n and not seen[r+dr][c+dc] and grid[r+dr][c+dc] != 0:
                        grid[r+dr][c+dc], seen[r+dr][c+dc] = index, True
                        lst.append((r+dr, c+dc))


        if not grid or not grid[0]:
            return 0

        m, n = len(grid), len(grid[0])
        connect(grid)
        count = collections.Counter()
        for row in grid:
            count += collections.Counter(row)

        for index, number in count.most_common(2):
            if index != 0:
                return number
        return 0


